// Filters function
function changeFilterActive () {
    $(document).on('click','.folio-filters li', function(){
        $(this).addClass('filter-active').siblings().removeClass('filter-active');
    })
}

// Projects (Portifolio) | filter-work
$(document).ready(function (){
    $('.filter').click(function (){
        const value = $(this).atrr('data-filter');

        if(value == "all") {
            $('.folio-box').show('1000')
        }else {
            $('.folio-box').not('.' + value).hide('1000')
            $('.folio-box').filter('.' + value).show('1000')
        }
    })
})

// Navbar | Sidebar LinksPage
function currentPage (e) {
    const sectionEl = document.querySelectorAll('.sections .section');

    switch(e) {
        case 0: 
            sectionEl[0].style.display = "block"
            sectionEl[1].style.display = "none"
            sectionEl[2].style.display = "none"
            sectionEl[3].style.display = "none"
            sectionEl[4].style.display = "none"
            break;
        case 1: 
            sectionEl[0].style.display = "none"
            sectionEl[1].style.display = "block"
            sectionEl[2].style.display = "none"
            sectionEl[3].style.display = "none"
            sectionEl[4].style.display = "none"
            break;
        case 2: 
            sectionEl[0].style.display = "none"
            sectionEl[1].style.display = "none"
            sectionEl[2].style.display = "block"
            sectionEl[3].style.display = "none"
            sectionEl[4].style.display = "none"
            break;
        case 3: 
            sectionEl[0].style.display = "none"
            sectionEl[1].style.display = "none"
            sectionEl[2].style.display = "none"
            sectionEl[3].style.display = "block"
            sectionEl[4].style.display = "none"
            break;
        case 4: 
            sectionEl[0].style.display = "none"
            sectionEl[1].style.display = "none"
            sectionEl[2].style.display = "none"
            sectionEl[3].style.display = "none"
            sectionEl[4].style.display = "block"
            break;
    }
}